fragments.content:
=LET(
    gsm, A2,              // GSM input cell
    sheets, B2,           // Number of sheets input cell
    // Different paper types have different conversion factors
    // Uncoated paper: ~0.8-1.0
    // Coated paper: ~0.9-1.1
    // Card stock: ~1.0-1.2
    microns_per_gsm, 0.9, // More conservative average ratio
    sheet_microns, gsm * microns_per_gsm / 1000,
    total_mm, sheet_microns * sheets,
    IF(
        total_mm >= 1,
        ROUND(total_mm, 2) & " mm",
        ROUND(total_mm * 1000, 2) & " microns"
    )
)

